import logo from './logo.svg';
import './App.css';
import { useState } from 'react';

function App() {
  const[data, getdata] = useState([])

  const req = function(){
    fetch("https://ghibliapi.herokuapp.com/films")
    .then(function(res){
      return res.json()
    })
    .then(function(json){
      getdata(json)
    })
  }
  return (
    <div className="App">
      <div>
        {data.map(function(item){
          <img src={item.image}></img>
        })}
      </div>
      <button onClick={req}></button>
    </div>

  );
}

export default App;
