import logo from './logo.svg';
import './App.css';
import { useEffect, useState } from 'react';

function App() {
  const[data, getdata] = useState([])
  const [datacount,setdatacount] = useState(0)

  const req = function(){
    setdatacount(datacount+1)

    fetch("https://ghibliapi.herokuapp.com/films")
    .then(function(res){
      return res.json()
    })
    .then(function(json){

      getdata(json)
    })
    if(datacount%2==0 && datacount!==0){
      getdata(getdata(getdata(data.push(0))))
    }
  }

  console.log(data)
  return (
    <div className="App">
    <div>

        {data.map(function(item){
          if(data.length>0){
          return(
            <div className='main'>
              <img src={item.image} className="nkar"></img>
            <div className='title'>{item.title}</div>
            <div className='midle'>Director: {item.director}</div>
            <div className='midle'>Producer: {item.producer}</div>
            <div className='midle'>Release Date: {item.release_date}</div>
            <div className='desc'>{item.description}</div>
            </div>
          )
          }

        })}
     </div>
      <button onClick={req} className="btn">{datacount>0? "delete List":"Get List"}</button>
    </div>

  );
}

export default App;
