import logo from './logo.svg';
import './App.css';
import { useState } from 'react';

function App() {
  const[data, getdata] = useState([])

  const req = function(){
    fetch("https://ghibliapi.herokuapp.com/films")
    .then(function(res){
      return res.json()
    })
    .then(function(json){
      getdata(json)
    })
  }
  console.log(data)
  return (
    <div className="App">

        {data.map(function(item){
          <div>{item.title}</div>
        })}

      <button onClick={req}></button>
    </div>

  );
}

export default App;
